import java.util.Scanner;public class largestAndSecondlargest {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int[] arr = new int[n];        for (int i = 0; i < n; i++) {            arr[i] = sc.nextInt();        }        System.out.println(largest(arr));        System.out.println(secondLargest(arr));    }    public static int largest(int[] arr){        int largest = Integer.MIN_VALUE;        for (int i = 0; i < arr.length; i++) {            if(arr[i] > largest){                largest = arr[i];            }        }        return largest;    }    public static int secondLargest(int[] arr){        int first = Integer.MIN_VALUE;        int second = Integer.MIN_VALUE;        for(int num : arr){            if(num > first){                second = first;                first = num;            } else if (second < num && num != first) {                second = num;            }        }        return second;    }}