import java.util.Scanner;public class multiply6to9 {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int[] arr = new int[4];        int idx = 0;        for (int i = 6; i <= 9; i++) {            arr[idx++] = n*i;        }        int idx2 = 0;        for (int i = 6; i <= 9 ; i++) {            System.out.println(n + " * " + i + " = " + arr[idx2++]);        }    }}