import java.util.Scanner;public class FizzBuzz {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        String[] arr = new String[n];        for (int i = 0; i < n; i++) {            if(i%15 == 0){                arr[i] = "FizzBuzz";            } else if (i%5==0) {                arr[i] = "Buzz";            } else if (i%3 == 0) {                arr[i] = "Fizz";            } else{                arr[i] = Integer.toString(i+1);            }        }        for (int i = 0; i <n; i++) {            System.out.println("Position " + (i+1) + " = " + arr[i]);        }    }}