import java.util.Scanner;public class ReverseNumber {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        String input = sc.nextLine();        int length = input.length();        int[] digits = new int[length];        int index = 0;        for (int i = 0; i < length; i++) {            char ch = input.charAt(i);            if (Character.isDigit(ch)) {                digits[index++] = ch - '0';            }        }        int[] reversed = new int[index];        for (int i = 0; i < index; i++) {            reversed[i] = digits[index - 1 - i];        }        System.out.print("Reversed number: ");        for (int digit : reversed) {            System.out.print(digit);        }        System.out.println();    }}