import java.util.Scanner;public class digitFrequency {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        String input = sc.nextLine();        int[] frequency = new int[10];        for (int i = 0; i < input.length(); i++) {            char ch = input.charAt(i);            if (Character.isDigit(ch)) {                int digit = ch - '0';                frequency[digit]++;            }        }        for (int i = 0; i < 10; i++) {            if (frequency[i] > 0) {                System.out.println("Digit " + i + ": " + frequency[i]);            }        }    }}