import java.util.Scanner;public class firstandlast {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int[] arr = new int[5];        for (int i = 0; i <5; i++) {            arr[i] = sc.nextInt();        }        for (int i = 0; i < 5; i++) {            if(arr[i]>0){                if(arr[i]%2 == 0){                    System.out.println(arr[i] + " is an even number");                } else{                    System.out.println(arr[i] + " is an odd number");                }            } else if (arr[i] == 0) {                System.out.println(arr[i]);            } else{                System.out.println(arr[i]+ " is a negative number");            }        }        if(arr[0] == arr[4]){            System.out.println("first and last element are equal");        } else if(arr[0] < arr[4]){            System.out.println("first element is lesser than last element");        }else{            System.out.println("last element is greater than first element");        }    }}