import java.util.Scanner;public class meanheight {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        double[] arr = new double[11];        double h = 0.0;        for (int i = 0; i < 11; i++) {            arr[i] = sc.nextInt();        }        for (int i = 0; i < arr.length; i++) {            h += arr[i];        }        System.out.println(h/11);    }}