import java.util.Scanner;public class oddAndEven {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int[] arr = new int[n];        for (int i = 0; i < n; i++) {            arr[i] = i+1;        }        int[] odd = new int[n/2 + 1];        int[] even = new int[n/2 + 1];        int e = 0;        int o = 0;        for (int i = 0; i < n; i++) {            if(arr[i]%2 == 0){                even[e++] = arr[i];            } else {                odd[o++] = arr[i];            }        }        for (int i = 0; i < even.length; i++) {            System.out.print(even[i] + " ");        }        System.out.println();        for (int i = 0; i < odd.length; i++) {            System.out.print(odd[i] + " ");        }    }}