import java.util.Scanner;public class showAndSum {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        double[] arr = new double[10];        int i = 0;        while (true){            double n = sc.nextDouble();            if(n <= 0 ){                break;            } else{                arr[i] = n;            }            i++;        }        double sum = 0.0;        for (int j = 0; j < arr.length; j++) {            System.out.println(arr[j]);            sum += arr[j];        }        System.out.println("The sum of elements is " + sum );    }}